import React, { useEffect, useState } from "react";
import { useHistory, Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";
import axios from "axios";
import "./AddEmployee.css";

const AddEmployee = () => {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [eid, setEID] = useState("");
  const [errors, setErrors] = useState({});
  const history = useHistory();
  const { id } = useParams();

  const validateForm = () => {
    const errors = {};
    if (!name.trim()) {
      errors.name = "Name is required";
    }
    if (!age.trim()) {
      errors.age = "Age is required";
    }
    if (!eid.trim()) {
      errors.eid = "EID is required";
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const saveOrUpdateEmployee = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    const employee = { name, age, eid };
    let formData = new FormData();
    formData.append("name", name);
    formData.append("eID", eid);
    formData.append("age", age);

    try {
      if (id) {
        formData.append("employeeID", id);
        const response = await axios.post("http://localhost:54369/Employee/updateEmployee", formData);
        if (response.data === "True") {
          Swal.fire("Updated!", "Your employee has been updated.", "success");
        }
      } else {
        const response = await axios.post("http://localhost:54369/Employee/insertEmployee", formData);
        if (response.data === "True") {
          Swal.fire("Added!", "Your employee has been added.", "success");
        }
      }
      history.push("/list-employees");
    } catch (error) {
      console.error("Error saving/updating employee:", error);
      Swal.fire("Error!", "Failed to save/update the employee.", "error");
    }
  };

  useEffect(() => {
    if (id) {
      axios.get(`http://localhost:54369/Employee/getEmployeeByID?EmpID=${id}`)
        .then((response) => {
          setName(response.data.name);
          setAge(response.data.age);
          setEID(response.data.eid);
        })
        .catch((error) => {
          console.error("Error fetching employee data:", error);
          Swal.fire("Error!", "Failed to fetch employee data.", "error");
        });
    }
  }, [id]);

  const title = () => {
    return id ? <h2 className="text-center">Update Employee</h2> : <h2 className="text-center">Add Employee</h2>;
  };

  return (
    <div>
      <br /> <br />
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3 offset-md-3">
            {title()}
            <div className="card-body transparent-table">
              <form>
                <div className="form-group mb-2">
                  <label className="form-label">Name:</label>
                  <input
                    type="text"
                    placeholder="Enter Name"
                    name="name"
                    className={`form-control ${errors.name ? "is-invalid" : ""}`}
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                  />
                  <div className="invalid-feedback">{errors.name}</div>
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Employee Age:</label>
                  <input
                    type="text"
                    placeholder="Enter Employee Age"
                    name="age"
                    className={`form-control ${errors.age ? "is-invalid" : ""}`}
                    value={age}
                    onChange={(e) => setAge(e.target.value)}
                  />
                  <div className="invalid-feedback">{errors.age}</div>
                </div>

                <div className="form-group mb-2">
                  <label className="form-label">Employee Education ID:</label>
                  <input
                    type="text"
                    placeholder="Enter Employee EID"
                    name="eid"
                    className={`form-control ${errors.eid ? "is-invalid" : ""}`}
                    value={eid}
                    onChange={(e) => setEID(e.target.value)}
                  />
                  <div className="invalid-feedback">{errors.eid}</div>
                </div>
                <button className="btn btn-success" onClick={saveOrUpdateEmployee}>
                  Save
                </button>
                <Link to="/" className="btn btn-danger" style={{ marginLeft: "2px" }}>
                  Cancel
                </Link>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddEmployee;
