import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import EmployeeService from '../service/EmployeeService'
import './ListEmployee.css'
import Swal from 'sweetalert2';
import axios from 'axios';

const ListEmployee = () => {

    const [employees, setEmployees] = useState([])

    useEffect(() => {
        getAllEmployees();

    }, [])

    const getAllEmployees = () => {

        axios.get(`http://localhost:54369/Employee/getAllEmployees  `)
        .then(res => {
            console.log(res.data);
            setEmployees(res.data)
        })  
    }

    

    return (
        <div className='container'>
            <h2 className='text-center L1'>List Employees</h2>
            <Link to="/add-workout" className='btn btn-primary mb-2'>Add Employee</Link>

            <table className='table table-bordered table-striped '>
                <thead>
                    <th style={{ color: 'white' }}>Employee ID</th>
                    <th style={{ color: 'white' }}>Employee Name</th>
                    <th style={{ color: 'white' }}>Employee Age</th>
                    <th style={{ color: 'white' }}>EID</th>
                    <th style={{ color: 'white' }}>Actions</th>
                </thead>
                <tbody>
                    {
                        employees.map(
                            employee =>
                                <tr key={employee.id}>
                                    <td>{employee.id}</td>
                                    <td>{employee.name}</td>
                                    <td>{employee.age}</td>
                                    <td>{employee.eid}</td>
                                    <td>
                                        <Link className='btn btn-info' to={`/edit-workout/${employee.id}`}>Update</Link>
                                        <button className='btn btn-danger' style={{ marginLeft: '4px' }} onClick={() => deleteEmployee(employee.id)}>Delete</button>
                                    </td>
                                </tr>
                        )
                    }
                </tbody>

            </table>

        </div>
    )
}

export default ListEmployee;
