import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import E from '../service/WorkoutService'
import './ListWorkout.css'
import Swal from 'sweetalert2';

const ListEmployee = () => {

    const [workouts, setWorkouts] = useState([])

    useEffect(() => {
        getAllWorkouts();

    }, [])

    const getAllWorkouts = () => {
        WorkoutService.getAllWorkouts().then((response) => {
            setWorkouts(response.data)
            console.log(response.data);
        }).catch(error => {
            console.log(error);
        })
    }

    const deleteWorkout = (workoutId) => {
        Swal.fire({
            title: 'Are you sure?',
            text: 'You will not be able to recover this workout!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.isConfirmed) {
                WorkoutService.deleteWorkout(workoutId).then(() => {
                    getAllWorkouts();
                    Swal.fire(
                        'Deleted!',
                        'Your workout has been deleted.',
                        'success'
                    );
                }).catch(error => {
                    console.log(error);
                    Swal.fire(
                        'Error!',
                        'Failed to delete the workout.',
                        'error'
                    );
                });
            }
        });
    };


    return (
        <div className='container'>
            <h2 className='text-center L1'>List Workouts</h2>
            <Link to="/add-workout" className='btn btn-primary mb-2'>Add Workout</Link>

            <table className='table table-bordered table-striped '>
                <thead>
                    <th style={{ color: 'white' }}>Workout ID</th>
                    <th style={{ color: 'white' }}>Workout Name</th>
                    <th style={{ color: 'white' }}>Workout Description</th>
                    <th style={{ color: 'white' }}>Workout Time-Period</th>
                    <th style={{ color: 'white' }}>Actions</th>
                </thead>
                <tbody>
                    {
                        workouts.map(
                            workout =>
                                <tr key={workout.id}>
                                    <td>{workout.id}</td>
                                    <td>{workout.workoutName}</td>
                                    <td>{workout.description}</td>
                                    <td>{workout.timePeriod}</td>
                                    <td>
                                        <Link className='btn btn-info' to={`/edit-workout/${workout.id}`}>Update</Link>
                                        <button className='btn btn-danger' style={{ marginLeft: '4px' }} onClick={() => deleteWorkout(workout.id)}>Delete</button>
                                    </td>
                                </tr>
                        )
                    }
                </tbody>

            </table>

        </div>
    )
}

export default ListWorkout;
