import axios from 'axios';

const API_BASE_URL = 'http://localhost:54369/'; 

export const GetEmployeeDetails = async () => {
    const config = {
        method: 'get',
        url: `${API_BASE_URL}/getAllEmployees`,
    };
    return await axios.request(config).then((response) => {
        return response.data;
    }).catch((error) => {
        console.error("There was an error getting the employees!", error);
    });
};

export const GetEmployeeById = async (id) => {
    const config = {
        method: 'get',
        url: `${API_BASE_URL}/getEmployee/${id}`,
    };
    return await axios.request(config).then((response) => {
        return response.data;
    }).catch((error) => {
        console.error(`There was an error getting the employee with id ${id}!`, error);
    });
};

export const AddEmployee = async (employee) => {
    const config = {
        method: 'post',
        url: `${API_BASE_URL}/addEmployee`,
        data: employee,
    };
    return await axios.request(config).then((response) => {
        return response.data;
    }).catch((error) => {
        console.error("There was an error adding the employee!", error);
    });
};

export const UpdateEmployee = async (employee) => {
    const config = {
        method: 'put',
        url: `${API_BASE_URL}/updateEmployee`,
        data: employee,
    };
    return await axios.request(config).then((response) => {
        return response.data;
    }).catch((error) => {
        console.error("There was an error updating the employee!", error);
    });
};

export const DeleteEmployee = async (id) => {
    const config = {
        method: 'delete',
        url: `${API_BASE_URL}/deleteEmployee/${id}`,
    };
    return await axios.request(config).then((response) => {
        return response.data;
    }).catch((error) => {
        console.error(`There was an error deleting the employee with id ${id}!`, error);
    });
};

export default {
    GetEmployeeDetails,
    GetEmployeeById,
    AddEmployee,
    UpdateEmployee,
    DeleteEmployee,
};
